// Dependencies
import React, { Component } from "react";
import { Link } from "react-router-dom";
import Utils from "../utils/utils";

// Redux
import PropTypes from "prop-types";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";

// Material UI
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

// Custom Actions


// START IMPORT ACTIONS
import CompanyActions from "../redux/actions/CompanyActions";

// END IMPORT ACTIONS

/** APIs

* actionsCompany.create
*	@description CRUD ACTION create
*
* actionsCompany.get
*	@description CRUD ACTION get
*	@param ObjectId id - Id resource
*
* actionsCompany.update
*	@description CRUD ACTION update
*	@param ObjectId id - Id
*

**/

class CompanyEdit extends Component {
  // Init company
  constructor(props) {
    super(props);
    this.state = {
      company: {}
    };
  }

  // Load data on start
  componentDidMount() {
    if (this.props.match.params.id !== "new") {
      this.props.actionsCompany.loadCompany(this.props.match.params.id);
    }
    
  }

  // Insert props company in state
  componentWillReceiveProps(props) {
    this.setState(...this.state, {
      company: props.company
    });
  }

  // Save data
  save(event) {
    event.preventDefault();
    if (this.state.company._id) {
      this.props.actionsCompany.saveCompany(this.state.company).then(data => {
        this.props.history.push("/companys/");
      });
    } else {
      this.props.actionsCompany.createCompany(this.state.company).then(data => {
        this.props.history.push("/companys/");
      });
    }
  }

  // Show content
  render() {
    return (
      <div>
        <h1>Company Edit</h1>
        <form className="myForm" onSubmit={this.save.bind(this)}>

          
          <TextField
            id="Industry"
            label="Industry"
            value={this.state.company.Industry || ""}
            onChange={Utils.handleChange.bind(this, "company")}
            margin="normal"
            fullWidth
          />
          
          
          <TextField
            id="Name"
            label="Name"
            value={this.state.company.Name || ""}
            onChange={Utils.handleChange.bind(this, "company")}
            margin="normal"
            fullWidth
          />
          

          {/* Footer */}
          <div className="footer-card">
            <Link to="/companys/">Back to list</Link>

            <Button type="submit" variant="contained" color="primary">
              Save
            </Button>
          </div>
        </form>
      </div>
    );
  }
}

// Store actions
const mapDispatchToProps = function(dispatch) {
  return { 
    actionsCompany: bindActionCreators(CompanyActions, dispatch),
  };
};

// Validate types
CompanyEdit.propTypes = { 
  actionsCompany: PropTypes.object.isRequired,
};

// Get props from state
function mapStateToProps(state, ownProps) {
  return {
    company: state.CompanyEditReducer.company
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CompanyEdit);
